// Profile.js
import React, { useEffect, useState } from "react";
import axios from "axios";

const Profile = () => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Utilisez une fonction asynchrone pour effectuer la requête au backend
    const fetchUserData = async () => {
      try {
        // Ajoutez votre endpoint backend pour récupérer les données du profil
        const response = await axios.get(
          "http://localhost:4000/users/profile",
          {
            // Ajoutez les en-têtes nécessaires, comme le token d'authentification
            headers: {
              Authorization: `Bearer ${votreToken}`, // Remplacez "votreToken" par le token que vous avez stocké
            },
          }
        );

        // Mettez à jour l'état avec les données du profil
        setUserData(response.data);
      } catch (error) {
        console.error(
          "Erreur lors de la récupération des données du profil :",
          error
        );
      }
    };

    // Appelez la fonction pour récupérer les données du profil
    fetchUserData();
  }, []); // Utilisez une dépendance vide pour vous assurer que cela s'exécute une seule fois

  // Si les données du profil ne sont pas encore chargées, affichez un message de chargement
  if (!userData) {
    return <p>Chargement du profil...</p>;
  }

  // Si les données du profil sont disponibles, affichez-les
  return (
    <div>
      <h2>Profil de l'utilisateur</h2>
      <p>Nom: {userData.nom}</p>
      <p>Prénom: {userData.prenom}</p>
      <p>Email: {userData.email}</p>
      {/* Ajoutez d'autres champs du profil selon votre modèle de données */}
    </div>
  );
};

export default Profile;
