import React, { useEffect, useState } from "react";
import axios from "axios";
import Cookies from "js-cookie";

const Profile = () => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Récupérez le token du cookie
        const token = Cookies.get("token"); // Assurez-vous de mettre le bon nom de cookie

        // Vérifiez si le token commence par "Bearer " et ajustez-le si nécessaire
        const formattedToken =
          token && token.startsWith("Bearer ") ? token : `Bearer ${token}`;

        // Ajoutez le token aux en-têtes de la requête Axios
        const headers = formattedToken ? { Authorization: formattedToken } : {};

        // Effectuez la requête avec les en-têtes
        const response = await axios.get(
          "http://localhost:4000/users/profile",
          { headers }
        );

        setUserData(response.data);
      } catch (error) {
        console.error(
          "Erreur lors de la récupération des données du profil :",
          error
        );
      }
    };

    fetchUserData();
  }, []);

  if (!userData) {
    return <p>Chargement du profil...</p>;
  }

  return (
    <div>
      <h2>Profil de l'utilisateur</h2>
      <p>Nom: {userData.nom}</p>
      <p>Prénom: {userData.prenom}</p>
      <p>Email: {userData.email}</p>
    </div>
  );
};

export default Profile;
